// Function node (NOT Function Item)

// stable hash
function hashKey(title, company, url) {
  const s = (title || '') + '|' + (company || '') + '|' + (url || '');
  let h = 5381; 
  for (let i = 0; i < s.length; i++) h = ((h << 5) + h) + s.charCodeAt(i);
  return (h >>> 0).toString(16);
}

// ban list
const BAN = [
  /student\s*(assistant|helper)?/i,
  /\bstudent\b/i,
  /studenter/i,
  /\bpost[- ]?doc(toral)?\b/i,
  /\bpraktikant\b/i,
];

const input = $input.all();
const seen = new Set();
const out = [];

for (let i = 0; i < input.length; i++) {
  const src = input[i];
  const j = { ...(src.json || {}) }; // shallow clone

  // ban check
  const hay = ((j.title || '') + ' ' + (j.raw_description || '')).toLowerCase();
  if (BAN.some(re => re.test(hay))) continue;

  // dedupe
  const k = hashKey(
    (j.title || '').toLowerCase(),
    (j.company || '').toLowerCase(),
    (j.url || '').toLowerCase()
  );
  if (seen.has(k)) continue;
  seen.add(k);

  j.hash = k;

  // preserve pairing so downstream nodes don't complain
  out.push({
    json: j,
    pairedItem: { item: i }
  });
}

return out;

// === Prepare compact prompt input for the AI scoring agent ===

// Fetch your CV text from the earlier node
// (Change 'MapResume' to your actual node name that contains cv_text)
const cv_text = $('MapResume').item.json.cv_text;

const job = $json;

// limit long job text to avoid token waste
const maxDesc = 5000; // ~1000 words
const cleanDesc = (job.raw_description || '')
  .replace(/\s+/g, ' ')
  .trim()
  .slice(0, maxDesc);

// build a structured summary text block for LLM
const jobTextBlock = [
  `Job title: ${job.title || ''}`,
  `Company: ${job.company || ''}`,
  job.location ? `Location: ${job.location}` : '',
  job.seniority_level ? `Seniority: ${job.seniority_level}` : '',
  job.employment_type ? `Employment type: ${job.employment_type}` : '',
  job.industries ? `Industry: ${job.industries}` : '',
  job.job_function ? `Function: ${job.job_function}` : '',
  '',
  'Job Description:',
  cleanDesc,
].filter(Boolean).join('\n');

// return one item per job
return {
  job_text: jobTextBlock,
  cv_text: cv_text.slice(0, 10000), // safety cap
  job_meta: {
    title: job.title || '',
    company: job.company || '',
    url: job.url || job.apply_url || '',
    hash: job.hash || '',
    location: job.location || '',
    posted_at: job.posted_at || '',
    source: job.source || 'LinkedIn'
  }
};

// Function Item version – returns ONE object

let raw;

if (typeof $json.output === 'string') {
  raw = $json.output;
} else if ($json.choices?.[0]?.message?.content) {
  raw = $json.choices[0].message.content;
} else {
  raw = JSON.stringify($json);
}

raw = raw.trim();
raw = raw.replace(/^```json/i, '').replace(/```$/, '').trim();

let data;
try {
  data = JSON.parse(raw);
} catch (e) {
  const m = raw.match(/\{[\s\S]*\}/);
  if (m) {
    try { data = JSON.parse(m[0]); }
    catch (e2) { data = { parse_error: e2.message, raw }; }
  } else {
    data = { parse_error: e.message, raw };
  }
}

// Validate and fix score structure
if (data && data.score) {
  // Ensure all required score fields exist
  const score = data.score;
  score.background_match = score.background_match || 0;
  score.skills_overlap = score.skills_overlap || 0;
  score.experience_relevance = score.experience_relevance || 0;
  score.seniority = score.seniority || 0;
  score.language_requirement = score.language_requirement || 0;
  score.company_score = score.company_score || 0;

  // Calculate overall if missing
  if (!score.overall) {
    score.overall = 
      score.background_match + 
      score.skills_overlap + 
      score.experience_relevance + 
      score.seniority + 
      score.language_requirement + 
      score.company_score;
  }
}

// Ensure other required fields have defaults
if (data) {
  data.summary = data.summary || 'No summary available';
  data.keywords = data.keywords || [];
  data.fit_bullets = data.fit_bullets || [];
  data.connector = data.connector || 'No connector available';
}

// FIXED: Retrieve job_meta by referencing the Prepare AI Input node directly
let job_meta = {};

try {
  // Access the Prepare AI Input node directly using pairing
  job_meta = $('Prepare AI Input').item.json.job_meta || {};
} catch (e) {
  // Fallback methods if direct reference fails
  if ($json.job_meta) {
    job_meta = $json.job_meta;
  } else if ($input.item?.json?.job_meta) {
    job_meta = $input.item.json.job_meta;
  } else if ($item(0)?.json?.job_meta) {
    job_meta = $item(0).json.job_meta;
  }
}

// Combine parsed output with job metadata
return {
  output: data,
  job_meta: job_meta
};

const config = $('Config').item.json;
const keywords = JSON.parse(config.keywords);
const location = encodeURIComponent(config.location);
const altLocation = encodeURIComponent(config.alternativeLocation);
const geoId = config.geoId;
const filters = config.jobFilters;
const altFilters = config.alternativeJobFilters;

// Add time filter parameter (defaults to past 24 hours)
const timeFilter = config.timeFilter || 'r86400'; // r86400 = past 24 hours

const urls = keywords.map((kw, idx) => {
  const encodedKw = encodeURIComponent(kw);
  const loc = idx < 3 ? location : altLocation;
  const filter = idx < 3 ? filters : altFilters;

  return `https://www.linkedin.com/jobs/search?keywords=${encodedKw}&location=${loc}&f_TPR=${timeFilter}&position=1&pageNum=0`;
});

return [{
  json: {
    count: 100,
    scrapeCompany: true,
    urls: urls
  }
}];

